        setInputValue(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if  (inputValue === 'fi$pond')  {
            setAccessGranted(true);
        }   else    {
            setError('fi$er not recognized');
        }
        console.log('Submitted value:', inputValue);
