import { useState } from 'react'
import { CSSTransition } from 'react-transition-group';
import './Menu.css'

function Menu() {
    const [expanse, setExpanse] = useState(null);

    const menus = {
        id: 1, 
        title: 'Menu 1', 
        items: 
            [ 
            {id: 1, name: 'WHY', desc: 'Why because'}, 
            {id: 2, name: 'WHAT', desc: 'What because'}, 
            {id: 3, name: 'HOW', desc: 'How because'}, 
            {id: 4, name: 'THAT', desc: 'That because'}, 
        ],
    };

    const handleMenuClick = (menu) =>   {
        setExpanse(menu.id);
    };

    return (
        <nav className="menu" role="navigation" aria-label="WWHT">
            {Object.keys(menus).map((key) => (
                <div key = {key} className="menu-item">
                    <button onClick={() => handleMenuClick(key)} 
                    aria-expanded={expanse === key.id} 
                    aria-controls={`menu-${key.id}`}>{key.title}</button>
                    {(expanse === key.id) && (
                        <CSSTransition in={expanse === key.id} timeout={300} classNames="menu-transition">
                            <ul id={`menu-${key.id}`} className="expanded-menu">
                                {menus.items.map((item, index) => (
                                    <li key = {item.id}>
                                        <h4>{item.name}</h4>
                                        <p>{item.desc}</p>    
                                    </li>
                                ))}
                            </ul>
                        </CSSTransition>
                    )}
                </div>
            ))}
        </nav>
    );
}

export default Menu
