import { useState } from 'react'
import { CSSTransition } from 'react-transition-group';
import './Menu.css'

function Menu() {
    const [expanse, setExpanse] = useState(null);

    const menus = {
        id: 1, 
        title: 'Menu 1', 
        items: 
            [ 
            {id: 1, name: 'WHY', desc: 'Why because'}, 
            {id: 2, name: 'WHAT', desc: 'What because'}, 
            {id: 3, name: 'WHEN', desc: 'When because'}, 
            {id: 4, name: 'THAT', desc: 'That because'}, 
        ],
    };

    const handleMenuClick = (menus) =>   {
        setExpanse(menus.id);
    };

    return (
        <nav className="menu" role="navigation" aria-label="WWHT">
            {menus.map((menu) => (
                <div key = {menus.id} className={"menu-item"}>
                    <button onClick={() => handleMenuClick(menus)} 
                    aria-expanded={expanse === menus.id} 
                    aria-controls={`menu-${menus.id}`}>{menus.title}</button>
                    {(expanse === menus.id) && (
                        <CSSTransition in={expanse === menus.id} timeout={300} classNames="menu-transition">
                            <ul id={`menu-${menus.id}`} className="expanded-menu">
                                {menus.items.map((item, index) => (
                                    <li key = {index}>{item.desc}</li>
                                ))}
                            </ul>
                        </CSSTransition>
                    )}
                </div>
            ))}
        </nav>
    );
}

export default Menu
