import { useState } from 'react'
import './Menu.css'

function Menu() {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [selectedMenu, setSelectedMenu] = useState(null);

    const menus = [
        {id: 1, title: 'Menu 1', items: ['Item 1', 'Item 2', 'Item 3'] },
        {id: 2, title: 'Menu 2', items: ['Item 4', 'Item 5', 'Item 6'] },
        {id: 3, title: 'Menu 3', items: ['Item 7', 'Item 8', 'Item 9'] },
    ];

    const handleMenuClick = (menu) =>   {
        setSelectedMenu(menu);
        setDropdownOpen(!dropdownOpen);
    };

    const handleMenuBlur = () =>    {
        setDropdownOpen(false);
    };

    return (
        <nav className="menu">
            {menus.map((menu) => (
                <div key = {menu.id} className={`menu-item $ {dropdownOpen && selectedMenu === menu ? 'active' : ''}`}>
                    <button onClick={() => handleMenuClick(menu)}>{menu.title}</button>
                    {(selectedMenu === menu && dropdownOpen) && (
                        <ul className="dropdown-menu">
                            {menu.items.map((item, index) => (
                                <li key = {index}>{item}</li>
                            ))}
                        </ul>
                    )}
                </div>
            ))}
        </nav>
    );
}

export default Menu
