import { useState } from 'react'
import { CSSTransition } from 'react-transition-group';
import './Menu.css'

function Menu() {
    const [expanse, setExpanse] = useState(null);

    const menus = [
            {id: 1, name: 'WHY', desc: 'Why because'}, 
            {id: 2, name: 'WHAT', desc: 'What because'}, 
            {id: 3, name: 'HOW', desc: 'How because'}, 
            {id: 4, name: 'THAT', desc: 'That because'}, 
        ];

    const handleMenuClick = (menu) =>   {
        setExpanse(menu.id);
    };

    return (
        <nav className="menu" role="navigation" aria-label="WWHT">
            {menus.map((menu) => (
                <div key = {menu.id} className="menu-item">
                    <button onClick={() => handleMenuClick(menu)} 
                    aria-expanded={expanse === menu.id} 
                    aria-controls={`menu-${menu.id}`}>{menu.title}</button>
                    {(expanse === menu.id) && (
                        <CSSTransition in={expanse === menu.id} timeout={3000} classNames="menu-transition">
                            <ul id={`menu-${menu.id}`} className="expanded-menu">
                                {menus.map((item, index) => (
                                    <li key = {item.id}>
                                        <h4>{item.name}</h4>
                                        <p>{item.desc}</p>    
                                    </li>
                                ))}
                            </ul>
                        </CSSTransition>
                    )}
                </div>
            ))}
        </nav>
    );
}

export default Menu
