import { useState } from 'react';
import './Contactus.css'

export default function Contactus() {

    const [drop, setDrop] = useState("")
    const dropdown = [
        {id:1, title:"Disclaimers", items:[]},
        {id:2, title:"Developer?", items:[]},
        {id:3, title:"Customer Service", items:[]}
    ];
    const handleDropClick = (menu) =>   {
        if (drop === menu.id) {
            setDrop(null);
        }   else    {
            setDrop(menu.id);
        }
    };

    return (
        <nav className = "dropdown">
            {dropdown.map((droppings) => (
                <div className="contactus">
                    <button 
                    onClick = {() => handleMenuClick(droppings)} > {droppings.title} </button>

                <ul className="link">
                    <li><a href = "./Disclaimer"><div className="disclaimer">Disclaimer</div></a></li>
                    <li><a href = "./Developer"><div className="developer">Developer?</div></a></li>
                    <li><a href = "./Faq"><div className="faq">Customer Service</div></a></li>
                </ul>
            </div>
            ))};
        </nav>
    );
}
