import { useState } from 'react'
import './Menu.css'

function Menu() {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [selectedMenu, setSelectedMenu] = useState(null);

    const handleMenuClick = (menu) =>   {
        setSelectedMenu(menu);
        setDropdownOpen(!dropdownOpen);
    };

    return (
        <nav className="menu">
            <ul>
                <li className="menu-item">
                    <span href="#" className="menu-link">Why?</span>
                </li>
                <li className={`"menu-item dropdown" ${dropdownOpen ? 'open' : ''}`}>
                    <span href="#" className="menu-link dropdown-toggle" onClick={handleDropdownToggle}>How?</span>
                    <ul className="dropdown-menu">
                        <li><span href="#"></span>Point 1</li>
                        <li><span href="#"></span>Point 2</li>
                        <li><span href="#"></span>Point 3</li>
                    </ul>
                </li>
                <li className="menu-item dropdown">
                    <span href="#" className="menu-link dropdown-toggle">Services</span>
                    <ul className="dropdown-menu">
                        <li><span href="#"></span>todo</li>
                        <li><span href="#"></span>safe</li>
                        <li><span href="#"></span>relax</li>
                    </ul>
                </li>
            </ul>
        </nav>
    );
}

export default Menu
