import { useState } from 'react'
import { CSSTransition } from 'react-transition-group';
import './Menu.css'

function Menu() {
    const [expanse, setExpanse] = useState(null);

    const menus = [
        {id: 1, title: 'Menu 1', items: ['Item 1', 'Item 2', 'Item 3'] },
        {id: 2, title: 'Menu 2', items: ['Item 4', 'Item 5', 'Item 6'] },
        {id: 3, title: 'Menu 3', items: ['Item 7', 'Item 8', 'Item 9'] },
    ];

    const handleMenuClick = (menu) =>   {
        setExpanse(menu.id);
    };

    return (
        <nav className="menu" role="navigation" aria-label="WWHT">
            {menus.map((menu) => (
                <div key = {menu.id} className={"menu-item"}>
                    <button onClick={() => handleMenuClick(menu)} 
                    aria-expanded={expanse === menu.id} 
                    aria-controls={`menu-${menu.id}`}>{menu.title}</button>
                    {(expanse === menu.id) && (
                        <CSSTransition in={expanse === menu.id} timeout={300} classNames="menu-transition">
                            <ul id={`menu-${menu.id}`} className="expanded-menu">
                                {menu.items.map((item, index) => (
                                    <li key = {index}>{item.name}</li>
                                ))}
                            </ul>
                        </CSSTransition>
                    )}
                </div>
            ))}
        </nav>
    );
}

export default Menu
