import { useState } from 'react';
import './Contactus.css'

export default function Contactus() {

    const [drop, setDrop] = useState("")
    const dropdown = [
        {id:1, title:"Disclaimers", items:[<a href = "./Disclaimer"><div className="disclaimer">Disclaimer</div></a>]},
        {id:2, title:"Developer?", items:[<a href = "./Developer"><div className="developer">Developer?</div></a>]},
        {id:3, title:"Customer Service", items:[<a href = "./Faq"><div className="faq">Customer Service</div></a>]}
    ];
    const handleDropClick = (droppings) =>   {
        if (drop === droppings.id) {
            setDrop(null);
        }   else    {
            setDrop(droppings.id);
        }
    };

    return (
        <nav className = "dropdown">
            {dropdown.map((droppings) => (
                <div key = {droppings.id} className="contactus">
                    <button 
                    onClick = {() => handleDropClick(droppings)} > {droppings.title} </button>
                        {( drop === handleDropClick(droppings.id)) &&  (
                            <ul id = {`dropdown-${droppings.id}`} 
                            className = "links">
                                {droppings.items.map((item, index) => (
                                    <li key = {item.id}>
                                        {item}
                                    </li>
                                ))}
                            </ul>
                        )}
                </div>
            ))};
        </nav>
    );
}
